package Package.logicprogram;public class HarshadNumber {    public static void main(String []args) {        int n = 234;        int sum = 0, temp = n;        while (temp > 0) {            sum = sum + (temp % 10);            temp = temp / 10;        }        if (n % sum == 0) {            System.out.println(n + " is a Harshad Number");        } else {            System.out.println(n + " is NOT a Harshad Number");        }    }}