package Package.logicprogram;public class LargestSmallestDigit {    public static void main(String args[]) {        int n = 58264;        int largest = 0, smallest = 9, temp = n;        while (temp > 0) {            int digit = temp % 10;            if (digit > largest) {                largest = digit;            }            if (digit < smallest) {                smallest = digit;            }            temp = temp / 10;        }        System.out.println("Largest digit = " + largest);        System.out.println("Smallest digit = " + smallest);    }}