package Package.logicprogram;public class SumEvenOddDigits {    public static void main(String args[]) {        int n = 44446677;        int evenSum = 0, oddSum = 0, temp = n;        while (temp > 0) {            int digit = temp % 10;            if (digit % 2 == 0) {                evenSum = evenSum + digit;            } else {                oddSum = oddSum + digit;            }            temp = temp / 10;        }        System.out.println("Sum of even digits = " + evenSum);        System.out.println("Sum of odd digits = " + oddSum);    }}